import React from 'react';


export function SongChoser() {
    return(
        <div>
        <Sound path={'audio.mp3'}/>
        </div>
    )

}

export function Sound(Path) {
    const url = URL.createObjectURL(Path)

    let audio = new Audio(url)
  
    const start = () => {
      audio.play()
    }
  
    return (
      < div >
        <button onClick={start}>Play</button>
      </div >
    );
  }
