import { Footer } from "./Footer/Footer";
import { SongList } from "./SongList";
import React,{ Component }  from 'react';
import { StatusBar } from "./Footer/StatusBar";
import { Sound } from "./Sound";


export class ControlPanel extends Component {
  
  constructor(props) {
    super(props);
    this.handleSongNameChange = this.handleSongNameChange.bind(this);
    this.state = {songName: ''};
  }
  
  handleSongNameChange(newSongName) {
    this.setState({songName: newSongName});
  }
   
  render() {
    return (
        <React.Fragment>
          <SongList songName={this.state.songName} 
             handleSongNameChange={this.handleSongNameChange}/>
          <StatusBar songName={this.state.songName} />
        </React.Fragment>
    );
  }
}
