import React from 'react';
let currentAudio;

export function pauseButton()
{
  currentAudio.pause();
}

export function playButton()
{
  currentAudio.play();
}

export function Sound({Path, Name}) {
    let audio = new Audio(Path)
    let name = Name;

    const start = async () => {
        try {
            if(currentAudio != null)
            {
            currentAudio.pause();
            }
            await audio.play();
            currentAudio = audio;
            console.log('Playing ' + name);
          } catch (err) {
            console.log('Failed to play...' + err);
          }
    }
  
    return (
      < div >
        <button onClick={start}>{name}</button>
      </div >
    );
  }
