import React, { Component } from 'react';
import { useState } from 'react';

let currentAudio;
const hoverColor = {background:'green'}
const defaultColor = {background: 'white'}

class Sound extends Component{
  constructor(props)
  {
    super(props);
    this.start = this.start.bind(this);
  }
   start = async (path) => {
    let audio = new Audio(path)
    try {
        if(currentAudio != null)
        {
        currentAudio.pause();
        currentAudio.load();
        }
        await audio.play();
        currentAudio = audio;
      } catch (err) {
      }
}
  
    render()
    {
    return (
        <div style={color} onClick={this.start}  onMouseOver={() => setColor(hoverColor)} onMouseLeave={() => setColor(defaultColor)}>
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
        </div>
    );
    }
  }



  export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleMouseOver (e) {
  e.target.style.background = 'red';
}

function handleMouseLeave (e) {
  e.target.style.background = 'white';
}
