import React from 'react';
let currentAudio;

export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleBoxToggle()
{
  this.setState({ showBox: !this.state.showBox });
}

function handleHover () {
  e.target.style.background = 'red';
}


export function Sound(props) {
  
    let audio = new Audio(props.path)

    const start = async () => {
        try {
            if(currentAudio != null)
            {
            currentAudio.pause();
            currentAudio.load();
            }
            await audio.play();
            currentAudio = audio;
            console.log('Playing ' + props.name);
          } catch (err) {
            console.log('Failed to play...' + err);
          }
    }
    
    return (
      <div onMouseOver={handleHover}>
        <body onClick={start} >
        <a>{props.name}</a>
        <a>{props.artist}</a>
        <a></a>
        </body>
      </div >
    );
  }
