import { useEffect, useState } from "react";
import { SpinnerLoading } from "../../Utils/SpinnerLoading";
import { ResumeButton, PauseButton, Sound, getCurrentSongName, handlePauseButton, start } from "./Sound";

let index;
let songList;


export const SongList = () => {

  const [songs, setSongs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);

  useEffect(() => {
    const fetchSongs = async() => {
      const baseUrl = "http://localhost:8080/api/songs";

      const url = `${baseUrl}`

      const response = await fetch(url);

      if(!response.ok)
      {
        throw new Error('Oh shit!');
      }

      const responseJson = await response.json();
      const responseData = responseJson._embedded.songs;

      const loadedSongs = [];

      for(const key in responseData)
      {
        loadedSongs.push({
          id: responseData[key].id,
          name: responseData[key].name,
          artist: responseData[key].artist,
          url: responseData[key].url
        });
      }
      setSongs(loadedSongs);
      setIsLoading(false);
    };
    fetchSongs().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    })
  }, []);

  if (isLoading) {
    return (
      <SpinnerLoading/>
    )
  }

  songList=[];
  songs.forEach((song,index)=>{
  songList.push(<Sound key = {song.id} id = {song.id} path = {song.url} name = {song.name} artist = {song.artist} />)
})
    return (
    <>
    <div>
    {songList}
    </div>

    <PauseButton/>
    <ResumeButton/>
    <NextButton/>
    </>
    );

};

function handleNextButtonClick() 
{
  let key = 0;
  handlePauseButton();
  console.log(getCurrentSongName());
  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.name == getCurrentSongName)
    {
      key = songList[i].props.id + 1;
      console.log(key);
    }

  }

  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.id === key + 1)
    {
      start(songList[i].props.path);  
    }
  }
}

function NextButton() 
{
return(
    <button onClick={handleNextButtonClick}>Next</button>
)
}
