import { initializeApp } from "firebase/app";
import { useEffect, useState } from "react";
import { SpinnerLoading } from "../../Utils/SpinnerLoading";
import { StatusBar } from "./Footer/StatusBar";
import { Sound, getCurrentSongName, start, pause, play, restart } from "./Sound";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";

let songList;

export function SongList() {


  const [songName, setSongName] = useState('');
  const [artistName, setArtistName] = useState('');
  const [songs, setSongs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);
  const [progress, setProgress] = useState(0);
  const [songList, setSongList] = useState();

  useEffect(() => {
    const fetchSongs = async() => {
      const baseUrl = "http://localhost:8080/api/songs";

      const url = `${baseUrl}`

      const response = await fetch(url);

      if(!response.ok)
      {
        throw new Error('Oh shit!');
      }

      const responseJson = await response.json();
      const responseData = responseJson._embedded.songs;
      console.log(responseData);

      const loadedSongs = [];

      for(const key in responseData)
      {
        loadedSongs.push({
          id: responseData[key].id,
          name: responseData[key].name,
          artist: responseData[key].artist,
          url: responseData[key].url
        });
      }
      setSongs(loadedSongs);
      setIsLoading(false);
    };
    fetchSongs().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    })
  }, []);

  if (isLoading) {
    return (
      <SpinnerLoading/>
    )
  }

  let results=[];
  songs.forEach((song,index)=>{
  results.push(<Sound key = {song.id} path = {song.url} name = {song.name} artist = {song.artist} setSongName = {setSongName} setArtistName = {setArtistName}/>)
  setSongList(results);
})
    return (
    <>
    {songList}
    <div>
    <StatusBar
    songName={songName} 
    artistName={artistName}/>
    <PreviousButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    <PauseButton/>
    <PlayButton/>
    <NextButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    </div>
    </>
    );

     function NextButton() {
      return <button onClick={handleNextButtonClick}>Next</button>;
    }

    function handleNextButtonClick() {
      getNextSong();
    }

    function PauseButton() {
      return (
          <button onClick={handlePauseButton}>Pause</button>
      );
    }

    function handlePauseButton() {
          pause();
    }

    function PlayButton() {
      return (
          <button onClick={handlePlayButton}>Play</button>
      );
    }

    function handlePlayButton() {
        play();
        initializeApp();
    }

    function PreviousButton() {
      return <button onClick={handlePreviousButtonClick}>Prev</button>;
    }

    function handlePreviousButtonClick() {
      getPreviousSong();
    }

    function getNextSong() 
{
  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === songList.length - 1)
      start(songList[0].props);
      else
      start(songList[i + 1].props);
      break;
    }
  }
}

function getPreviousSong() 
{
  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === 0)
      start(songList[songList.length - 1].props);
      else
      start(songList[i - 1].props);
      break;
    }
  }
}

};
