import React from 'react';
let currentAudio;

export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

export function Sound({path, name, artist, }) {
    let audio = new Audio(path)

    const start = async () => {
        try {
            if(currentAudio != null)
            {
            currentAudio.pause();
            }
            await audio.play();
            currentAudio = audio;
            console.log('Playing ' + name);
          } catch (err) {
            console.log('Failed to play...' + err);
          }
    }
  
    return (
      < div >
        <body>
        <button onClick={start}>{name}</button>
        <a>{artist}</a>
        <a></a>
        </body>
      </div >
    );
  }
