class ControlPanel extends Component {
  
  constructor(props) {
    super(props);
    this.handleSongNameChange = this.handleSongNameChange.bind(this);
    this.state = {songName: ''};
  }
  
  handleSongNameChange(newSongName) {
    this.setState({songName: newSongName});
  }
   
  render() {
    return (
        <React.Fragment>
          <B text={this.state.text} 
             handleSongNameChange={this.handleSongNameChange}/>
          <C text={this.state.text} />
        </React.Fragment>
    );
  }
}
