import React from 'react';
import { useState } from 'react';

let currentAudio;
const white = {background:'white'}
const blue = {background: 'blue'}


export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleMouseOver (e) {
  e.target.style.background = 'red';
}

function handleMouseLeave (e) {
  e.target.style.background = 'white';
}


export function Sound(props) {
    const [color, setColor] = useState(white)
    let audio = new Audio(props.path)

    const start = async () => {
        try {
            if(currentAudio != null)
            {
            currentAudio.pause();
            currentAudio.load();
            }
            await audio.play();
            currentAudio = audio;
            console.log('Playing ' + props.name);
          } catch (err) {
            console.log('Failed to play...' + err);
          }
    }
    
    return (
        <div style={color} onClick={start}  onMouseOver={() => setColor(blue)} onMouseLeave={() => setColor(white)}>
        <a>{props.name}</a>
        <a>{props.artist}</a>
        </div>
    );
  }
