import { initializeApp } from "firebase/app";
import { useEffect, useState } from "react";
import { uploadFiles } from "../../../CloudUpload";
import { SpinnerLoading } from "../../Utils/SpinnerLoading";
import { StatusBar } from "./Footer/StatusBar";
import { Sound, getCurrentSongName, start, pause, play, restart } from "./Sound";

let songList;

export function SongList() {


  const [songName, setSongName] = useState('');
  const [artistName, setArtistName] = useState('');
  const [songs, setSongs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState(null);

  useEffect(() => {
    const fetchSongs = async() => {
      const baseUrl = "http://localhost:8080/api/songs";

      const url = `${baseUrl}`

      const response = await fetch(url);

      if(!response.ok)
      {
        throw new Error('Oh shit!');
      }

      const responseJson = await response.json();
      const responseData = responseJson._embedded.songs;

      const loadedSongs = [];

      for(const key in responseData)
      {
        loadedSongs.push({
          id: responseData[key].id,
          name: responseData[key].name,
          artist: responseData[key].artist,
          url: responseData[key].url
        });
      }
      setSongs(loadedSongs);
      setIsLoading(false);
    };
    fetchSongs().catch((error) => {
      setIsLoading(false);
      setHttpError(error.message);
    })
  }, []);

  if (isLoading) {
    return (
      <SpinnerLoading/>
    )
  }

  songList=[];
  songs.forEach((song,index)=>{
  songList.push(<Sound key = {song.id} path = {song.url} name = {song.name} artist = {song.artist} setSongName = {setSongName} setArtistName = {setArtistName}/>)
})
    return (
    <>
    {songList}
    <div>
    <StatusBar
    songName={songName} 
    artistName={artistName}/>
    <PreviousButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    <PauseButton/>
    <PlayButton/>
    <NextButton setSongName = {setSongName} setArtistName = {setArtistName}/>
    </div>
    </>
    );

     function NextButton() {
      return <button onClick={handleNextButtonClick}>Next</button>;
    }

    function handleNextButtonClick() {
      getNextSong();
    }

    function PauseButton() {
      return (
          <button onClick={handlePauseButton}>Pause</button>
      );
    }

    function handlePauseButton() {
          pause();
    }

    function PlayButton() {
      return (
          <button onClick={handlePlayButton}>Play</button>
      );
    }

    function handlePlayButton() {
        play();
        initializeApp();
    }

    function PreviousButton() {
      return <button onClick={handlePreviousButtonClick}>Prev</button>;
    }

    function handlePreviousButtonClick() {
      getPreviousSong();
    }

};

export function getNextSong() 
{
  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === songList.length - 1)
      start(songList[0].props);
      else
      start(songList[i + 1].props);
      break;
    }
  }
}

export function getPreviousSong() 
{
  for(let i = 0; i < songList.length; i++)
  {
    if(songList[i].props.name === getCurrentSongName())
    {
      restart();
      if(i === 0)
      start(songList[songList.length - 1].props);
      else
      start(songList[i - 1].props);
      break;
    }
  }
}

function App() {
      // State to store uploaded file
      const [file, setFile] = useState("");
   
      // progress
      const [percent, setPercent] = useState(0);
   
      // Handle file upload event and update state
      function handleChange(event) {
          setFile(event.target.files[0]);
      }
   
      const handleUpload = () => {
          if (!file) {
              alert("Please upload an image first!");
          }
   
          const storageRef = ref(storage, `/files/${file.name}`);
   
          // progress can be paused and resumed. It also exposes progress updates.
          // Receives the storage reference and the file to upload.
          const uploadTask = uploadBytesResumable(storageRef, file);
   
          uploadTask.on(
              "state_changed",
              (snapshot) => {
                  const percent = Math.round(
                      (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                  );
   
                  // update progress
                  setPercent(percent);
              },
              (err) => console.log(err),
              () => {
                  // download url
                  getDownloadURL(uploadTask.snapshot.ref).then((url) => {
                      console.log(url);
                  });
              }
          );
      };
   
      return (
          <div>
              <input type="file" onChange={handleChange} accept="/image/*" />
              <button onClick={handleUpload}>Upload to Firebase</button>
              <p>{percent} "% done"</p>
          </div>
      );
  }
