import React, { Component } from "react";

let currentAudio;

export class Sound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "white",
      textColor: "black"
    };
    this.handleMouseEnter = this.handleMouseEnter.bind(this);
    this.handleMouseExit = this.handleMouseExit.bind(this);
  }

  render() {
    const style = {
      background: this.state.background,
      color: this.state.textColor,
    };

    return (
      <div
        style={style}
        onClick={this.start}
        onMouseOver={this.handleMouseEnter}
        onMouseLeave={this.handleMouseExit}
      >
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
      </div>
    );
  }

  start = async () => {
    let audio = new Audio(this.props.path);
    try {
      if (currentAudio != null) {
        currentAudio.pause();
        currentAudio.load();
      }
      await audio.play();
      currentAudio = audio;
    } catch (err) {}
  };

  handleMouseEnter() {
    this.setState({ background: "green" });
  }

  handleMouseExit() {
    this.setState({ background: "white" });
  }
}

export class PauseButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isPaused: true
    };
    this.handlePauseButtonClicked = this.handlePauseButtonClicked.bind(this);
  }

  render() {

    return (
      <PauseButton/>
    );
}

PauseButton() {
  return (
      <button onClick={this.handlePauseButtonClicked}>Pause</button>
  );
}

handlePauseButtonClicked() {
  if (currentAudio != null) {
    currentAudio.pause();
  } else console.log("Nothing to pause");
}
}
