import React from 'react';
let currentAudio;

export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleMouseOver (e) {
  e.target.style.background = 'red';
}

function handleMouseLeave (e) {
  e.target.style.background = 'white';
}

class Song extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      path: '',
      name: '',
      artist: ''
    };
  }

  render() {
    return (
      <div onClick={start}  onMouseOver={handleMouseOver} onMouseLeave={handleMouseLeave}>
      <a>{props.name}</a>
      <a>{props.artist}</a>
      </div>
  );
  }
}

export function Sound(props) {
  
    let audio = new Audio(props.path)

    const start = async () => {
        try {
            if(currentAudio != null)
            {
            currentAudio.pause();
            currentAudio.load();
            }
            await audio.play();
            currentAudio = audio;
            console.log('Playing ' + props.name);
          } catch (err) {
            console.log('Failed to play...' + err);
          }
    }
    
   
  }
