import React from 'react';
let currentAudio;

export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleMouseOver (e) {
  e.target.style.background = 'red';
}

function handleMouseLeave (e) {
  e.target.style.background = 'white';
}

export class Song extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      path: props.path,
      name: props.name,
      artist: props.artist
    };
  }

  render() {
    return (
      <div onClick={start(this.state.path)}  onMouseOver={handleMouseOver} onMouseLeave={handleMouseLeave}>
      <a>{this.state.name}</a>
      <a>{this.state.artist}</a>
      </div>
  );
  }
}

  


