import path from "path";
import React, { Component } from "react";

let currentAudio;
let currentSongName;

export class Sound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "white",
      textColor: "black"
    };
    this.handleMouseEnter = this.handleMouseEnter.bind(this);
    this.handleMouseExit = this.handleMouseExit.bind(this);
  }

  render() {
    const style = {
      background: this.state.background,
      color: this.state.textColor,
    };

    return (
      <div
        style={style}
        onClick={(path) => { this.start() }}
        onMouseOver={this.handleMouseEnter}
        onMouseLeave={this.handleMouseExit}
      >
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
      </div>
    );
  }

  start = async (path) => {
    let audio = new Audio(path);
    try {
      if (currentAudio != null) {
        currentAudio.pause();
        currentAudio.load();
      }
      await audio.play();
      currentAudio = audio;
      currentSongName = this.props.name;

    } catch (err) {}
  };

  handleMouseEnter() {
    this.setState({ background: "green" });
  }

  handleMouseExit() {
    this.setState({ background: "white" });
  }
}

export function PauseButton() {
  return (
      <button onClick={handlePauseButton}>Pause</button>
  );
}

export function ResumeButton() {
  return (
      <button onClick={handleResumeButton}>Play</button>
  );
}

export function handlePauseButton() {
  if (currentAudio != null) {
    currentAudio.pause();
  } else console.log("Nothing to pause");
}

function handleResumeButton() {
  if (currentAudio != null) {
    currentAudio.play();
  } else console.log("Nothing to resume");
}

export function getCurrentSongName() {
  return currentSongName;
}
