import React from 'react';
import { useState } from 'react';

let currentAudio;
const hoverColor = {background:'green'}
const defaultColor = {background: 'white'}


export function Sound(props) {

  const start = async (path) => {
    let audio = new Audio(path)
    try {
        if(currentAudio != null)
        {
        currentAudio.pause();
        currentAudio.load();
        }
        await audio.play();
        currentAudio = audio;
      } catch (err) {
      }
}

    const [color, setColor] = useState(defaultColor)
    return (
        <div style={color} onClick={this.start(props.path)}  onMouseOver={() => setColor(hoverColor)} onMouseLeave={() => setColor(defaultColor)}>
        <a>{props.name}</a>
        <a>{props.artist}</a>
        </div>
    );
  }



  export function PauseButton()
{
  return (
    <div>
    <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton()
{
  return (
    <div>
    <button onClick={resume}>Play</button>
    </div>
  );
}

function pause()
{
  if(currentAudio != null)
  {
  currentAudio.pause();
  }
  else
  console.log("Nothing to pause");
}

function resume()
{
  if(currentAudio != null)
  {
  currentAudio.play();
  }
  else
  console.log("Nothing to resume");
}

function handleMouseOver (e) {
  e.target.style.background = 'red';
}

function handleMouseLeave (e) {
  e.target.style.background = 'white';
}
