import React, { Component } from "react";
import { useState } from "react";

let currentAudio;
const hoverColor = { background: "green" };
const defaultColor = { background: "white" };

export class Sound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "white",
      textColor: "black"
    }
    this.handleMouseEnter = this.handleMouseEnter.bind(this)
    this.handleMouseExit = this.handleMouseEnter.bind(this) 


  }

  start = async () => {
    let audio = new Audio(this.props.path);
    try {
      if (currentAudio != null) {
        currentAudio.pause();
        currentAudio.load();
      }
      await audio.play();
      currentAudio = audio;
    } catch (err) {}
  };

  handleMouseEnter() {
    this.setState({background : 'green'}) 
} 

handleMouseEnter() {
  this.setState({background : 'white'}) 
} 
  

  render() {
    const style = {
      background: this.state.background,
      color: this.state.textColor
    }
    return (
      <div style={style} onClick={this.start} onMouseOver={this.handleMouseEnter}>
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
      </div>
    );
  }
}

export function PauseButton() {
  return (
    <div>
      <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton() {
  return (
    <div>
      <button onClick={resume}>Play</button>
    </div>
  );
}

function pause() {
  if (currentAudio != null) {
    currentAudio.pause();
  } else console.log("Nothing to pause");
}

function resume() {
  if (currentAudio != null) {
    currentAudio.play();
  } else console.log("Nothing to resume");
}
