import React, { Component } from "react";

let currentAudio;
let currentSongName;

export class Sound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "white",
      textColor: "black"
    };
    this.handleMouseEnter = this.handleMouseEnter.bind(this);
    this.handleMouseExit = this.handleMouseExit.bind(this);
    this.handleSongClick = this.handleSongClick.bind(this);
    this.handleSongNameChange = this.handleSongNameChange.bind(this);
  }

  render() {
    const style = {
      background: this.state.background,
      color: this.state.textColor,
    };

    return (
      <div
        style={style}
        onClick={this.handleSongClick}
        onMouseOver={this.handleMouseEnter}
        onMouseLeave={this.handleMouseExit}
      >
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
      </div>
    );
  }

  handleSongNameChange(newSongName)
  {
    this.props.handleSongNameChange(newSongName);
  }

  handleSongClick() 
  {
    start(this.props);
    this.handleSongNameChange(this.props.name);
  }

  handleMouseEnter() {
    this.setState({ background: "green" });
  }

  handleMouseExit() {
    this.setState({ background: "white" });
  }
}

export async function start(props) {
  let audio = new Audio(props.path);
  currentSongName = props.name;
  try {
    if (currentAudio != null) {
      currentAudio.pause();
      currentAudio.load();
    }
    await audio.play();
    currentAudio = audio;
  } catch (err) {}
};

export function pause()
{
  if(currentAudio != null)
  currentAudio.pause();
}

export function play()
{
  if(currentAudio != null)
  currentAudio.play();
}


export function getCurrentSongName() {
  return currentSongName;
}
