import React, { Component } from "react";
import { useState } from "react";

let currentAudio;
const hoverColor = { background: "green" };
const defaultColor = { background: "white" };

export class Sound extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "green"
    }
    this.defaultColor = {background: "white"};
    this.hoverColor = {background: "green"};
  }

  start = async () => {
    let audio = new Audio(this.props.path);
    try {
      if (currentAudio != null) {
        currentAudio.pause();
        currentAudio.load();
      }
      await audio.play();
      currentAudio = audio;
    } catch (err) {}
  };

  render() {
    return (
      <div style="background-color:{this.state.background}" onClick={this.start}>
        <a>{this.props.name}</a>
        <a>{this.props.artist}</a>
      </div>
    );
  }
}

export function PauseButton() {
  return (
    <div>
      <button onClick={pause}>Pause</button>
    </div>
  );
}

export function ResumeButton() {
  return (
    <div>
      <button onClick={resume}>Play</button>
    </div>
  );
}

function pause() {
  if (currentAudio != null) {
    currentAudio.pause();
  } else console.log("Nothing to pause");
}

function resume() {
  if (currentAudio != null) {
    currentAudio.play();
  } else console.log("Nothing to resume");
}
